module clk_div (
    input CLK_in,
    input RST,
    output CLK_out
);
    reg counter = 0;
    always @(*) begin
        if (RST) begin
            counter <= 0;
        end else begin
            if (counter == 49) begin
                CLK_out <= 1;
                counter <= 0;
            end else begin
                CLK_out <= 0;
                counter <= counter + 1;
            end
        end
    end
endmodule

