    // First adder
    add_4bit add1(.A(A[3]), .B(B[3]), .C(C), .Sum(temp1));
    add_4bit add2(.A(A[2]), .B(B[2]), .C(C), .Sum(temp2));

    // Second adder
    add_4bit add3(.A(A[1]), .B(B[1]), .C(C), .Sum(temp1));
    add_4bit add4(.A(A[0]), .B(B[0]), .C(C), .Sum(Sum)));

    // Final adder
    add_4bit add5(.A(temp1), .B(temp2)), .C(C), .Sum(Sum));

endmodule

module add_4bit(A, B, C, Sum);
    input [7:0] A, B;
    output [7:0] Sum, C;

    wire [7:0] temp1, temp2;

    // First adder
    add_2 add1(.A(A[3]), .B(B[3]), .C(C), .Sum(temp1));
    add_2 add2(.A(A[2]), .B(B[2]), .C(C), .Sum(temp2));

    // Second adder
    add_2 add3(.A(A[1]), .B(B[1]), .C(C), .Sum(temp1));
    add_2 add4(.A(A[0]), .B(B[0]), .C(C), .Sum(Sum)));

    // Final adder
    add_2 add5(.A(temp1), .B(temp2)), .C(C), .Sum(Sum)));

endmodule

input [3:0] X;
    input [3:0] Y;
    output [7:0] P;

    wire [7:0] temp1, temp2;

    // First adder
    add_8bit add1(.A(X[0]), .B(Y[0]), .C(temp1[0]), .P(P[0]));

    // Second adder
    add_8bit add2(.A(X[1]), .B(Y[1]), .C(temp2[0]), .P(P[1]));

    // Final adder
    add_8bit add3(.A(X[2]), .B(Y[2]), .C(temp2[1]), .P(P[2]));

endmodule

module add_8bit(A, B, C, P);
    input [7:0] A;
    input [7:0] B;
    output [7:0] C;
    output [7:0] P;

    wire [7:0] sum;

    // First adder
    assign sum = A + B;
    assign C = (A[7] & B[7]) | (A[6] & B[6]) | (A[5] & B[5]) | (A[4] & B[4]);

    // Second adder
    assign sum[6:0] = A[6:0] + B[6:0];
    assign sum[7:1] = A[7:1] + B[7:1] + B[7:1];

    // Final adder
    assign P = sum[6:0] + sum[7:1];
endmodule

input [3:0] X;
    input [3:0] Y;
    output [7:0] P;

    wire [7:0] temp1, temp2;

    // First adder
    add_8bit add1(.A(X[0]), .B(Y[0]), .C(temp1[0]), .P(temp1));

    // Second adder
    add_8bit add2(.A(X[1]), .B(Y[1]), .C(temp2[0]), .P(temp2));

    // Final adder
    add_8bit add3(.A(temp1[1]), .B(temp2[1]), .C(temp2[1]), .P(temp2[1]));

    // Assignor
    assign P = {temp2[0], temp2[1], temp2[2], temp2[3], temp2[4], temp2[5], temp2[6], temp2[7]};

endmodule

module add_8bit(A, B, C, P);
    input [7:0] A, B;
    output [7:0] P, C;

    wire [7:0] temp1, temp2;

    // First adder
    add_4bit add1(.A(A[0]), .B(B[0]), .C(C), .P(temp1));

    // Second adder
    add_4bit add2(.A(A[1]), .B(B[1]), .C(C), .P(temp2));

    // Final adder
    add_4bit add3(.A(temp1[2]), .B(temp2[2]), .C(temp2[3]), .P(temp2[4]), .P(temp2[5]), .P(temp2[6]), .P(temp2[7]), .P(P)));

endmodule
