module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);
    // Interconnecting wires
    wire [1:0] state;
    // Instantiating submodules
    state_inst u_state_inst (
        .clk(clk),
        .rst_n(rst_n),
        .state(state)
    );
    wave_inst u_wave_inst (
        .clk(clk),
        .rst_n(rst_n),
        .state(state),
        .wave(wave)
    );
endmodule
module state_inst (
    input clk,
    input rst_n,
    output reg [1:0] state
);
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            state <= 2'b00;
        end
        else begin
            // State transitions
            state <= state;
        end
    end
endmodule
module wave_inst (
    input clk,
    input rst_n,
    input [1:0] state,
    output reg [4:0] wave
);
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            wave <= 5'b00000;
        end
        else begin
            // Waveform generation based on state
            case (state)
                2'b00: wave <= wave + 1;
                2'b01: wave <= wave - 1;
                default: wave <= wave;
            endcase
        end
    end
endmodule

