module pwm_duty_25 (
    input CLK_in,
    input RST,
    output reg PWM_out
);
    parameter TOP_COUNT = 100; // 100MHz input clock
    reg [6:0] count;
    always @(posedge CLK_in or posedge RST) begin
        if (RST) begin
            count <= 0;
            PWM_out <= 0;
        end else if (count == TOP_COUNT) begin
            count <= 0;
            PWM_out <= 0;
        end else begin
            count <= count + 1;
            if (count < (TOP_COUNT / 4)) begin
                PWM_out <= 1;
            end else begin
                PWM_out <= 0;
            end
        end
    end
endmodule
