    bitwise_and bitwise_and_unit(.a(a), .b(b), .result(result_internal));
    bitwise_or bitwise_or_unit(.a(a), .b(b), .result(result_internal));
    adder_4bit adder_unit(.a(a), .b(b), .result(result_internal), .carry_out(carry_borrow_internal));
    subtractor_4bit subtractor_unit(.a(a), .b(b), .result(result_internal), .borrow_out(carry_borrow_internal));
    assign result = result_internal;
    assign carry_borrow = carry_borrow_internal;
endmodule
module bitwise_and(a, b, result);
    input [3:0] a, b;
    output [3:0] result;
    and(result[0], a[0], b[0]);
