module adder_4bit(a, b, result, carry_out);
    input [3:0] a, b;
    output [3:0] result;
    output carry_out;
    wire [3:0] carry;
    full_adder fa0(.a(a[0]), .b(b[0]), .carry_in(1'b0), .result(result[0]), .carry_out(carry[0]));
    full_adder fa1(.a(a[1]), .b(b[1]), .carry_in(carry[0]), .result(result[1]), .carry_out(carry[1]));
    full_adder fa2(.a(a[2]), .b(b[2]), .carry_in(carry[1]), .result(result[2]), .carry_out(carry[2]));
    full_adder fa3(.a(a[3]), .b(b[3]), .carry_in(carry[2]), .result(result[3]), .carry_out(carry_out));
endmodule
module subtractor_4bit(a, b, result, borrow_out);
    input [3:0] a, b;
    output [3:0] result;
    output borrow_out;
    wire [3:0] carry;
    full_adder fa0(.a(a[0]), .b(~b[0]), .carry_in(1'b1), .result(result[0]), .carry_out(carry[0]));
    full_adder fa1(.a(a[1]), .b(~b[1]), .carry_in(carry[0]), .result(result[1]), .carry_out(carry[1]));
    full_adder fa2(.a(a[2]), .b(~b[2]), .carry_in(carry[1]), .result(result[2]), .carry_out(carry[2]));
    full_adder fa3(.a(a[3]), .b(~b[3]), .carry_in(carry[2]), .result(result[3]), .carry_out(carry_out));
    assign borrow_out = ~carry[3];
endmodule
module full_adder(a, b, carry_in, result, carry_out);
    input a, b, carry_in;
    output result, carry_out;
    wire half_sum, half_carry_1, half_carry_2;
    xor(half_sum, a, b);
    and(half_carry_1, a, b);
    xor(result, half_sum, carry_in);
    and(half_carry_2, half_sum, carry_in);
    or(carry_out, half_carry_1, half_carry_2);
endmodule

