module u_count_control (
    input               clk,
    input               rst,
    input               en,
    output reg          reset_count,
    output reg          increment_count
);
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            reset_count <= 1'b1;
            increment_count <= 1'b0;
        end
        else begin
            reset_count <= 1'b0;
            increment_count <= en;
        end
    end
endmodule
module u_counter (
    input               clk,
    input               rst,
    input               reset_count,
    input               increment_count,
    output reg  [3:0]   count
);
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            count <= 4'b0000;
        end
        else if (reset_count) begin
            count <= 4'b0000;
        end
        else if (increment_count) begin
            count <= count + 1;
        end
    end
endmodule
module u_overflow_detection (
    input               clk,
    input               rst,
    input       [3:0]   count,
    output reg          overflow
);
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            overflow <= 1'b0;
        end
        else if (count == 4'b1111) begin
            overflow <= 1'b1;
        end
        else begin
            overflow <= 1'b0;
        end
    end
endmodule

