module u_count_control (
    input               clk,
    input               rst,
    input               en,
    output reg  [3:0]   count_out
);
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            count_out <= 4'b0000;
        end else if (en) begin
            count_out <= count_out + 1;
        end
    end
endmodule
module u_overflow_detection (
    input               clk,
    input               rst,
    input               en,
    input       [3:0]   count_out,
    output reg          overflow
);
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            overflow <= 1'b0;
        end else if (en) begin
            overflow <= overflow | (count_out == 4'b1111);
        end
    end
endmodule

