module check_bit(in, in_not, valid_in, code_in, valid_out, code_out, highest_bit);
    input [7:0] in;
    input [7:0] in_not;
    input valid_in;
    input [2:0] code_in;
    output valid_out;
    output [2:0] code_out;
    output highest_bit;
    wire [7:0] and_w;
    wire [7:0] or_w;
    wire [7:0] not_w;
    wire [7:0] highest_bit_w;
    genvar i;
    generate
        for (i = 0; i < 8; i = i + 1) begin
            and(and_w[i], in_not[i], in[i]);
        end
    endgenerate
    generate
        for (i = 0; i < 8; i = i + 1) begin
            or(or_w[i], and_w[i], and_w[i - 1]);
        end
    endgenerate
    not(not_w[0], or_w[0]);
    generate
        for (i = 1; i < 8; i = i + 1) begin
            not(not_w[i], or_w[i]);
        end
    endgenerate
    generate
        for (i = 0; i < 8; i = i + 1) begin
            and(highest_bit_w[i], not_w[i], not_w[i - 1]);
        end
    endgenerate
    and(valid_out, valid_in, or_w[7]);
    or(code_out, code_in, or_w[7]);
    and(highest_bit, valid_in, highest_bit_w[7]);
endmodule

