priority_encoder(in, code, valid);
    input [7:0] in;
    output [2:0] code;
    output valid;
    wire [7:0] cb0, cb1, cb2, cb3, cb4, cb5, cb6, cb7;
    wire [7:0] valid_chain;
    // check_bit instances
    check_bit cb0_inst(.in(in), .valid(valid_chain[0]), .code(3'b000), .cb(cb0));
    check_bit cb1_inst(.in(in), .valid(valid_chain[1]), .code(3'b001), .cb(cb1));
    check_bit cb2_inst(.in(in), .valid(valid_chain[2]), .code(3'b010), .cb(cb2));
    check_bit cb3_inst(.in(in), .valid(valid_chain[3]), .code(3'b011), .cb(cb3));
    check_bit cb4_inst(.in(in), .valid(valid_chain[4]), .code(3'b100), .cb(cb4));
    check_bit cb5_inst(.in(in), .valid(valid_chain[5]), .code(3'b101), .cb(cb5));
    check_bit cb6_inst(.in(in), .valid(valid_chain[6]), .code(3'b110), .cb(cb6));
    check_bit cb7_inst(.in(in), .valid(valid_chain[7]), .code(3'b111), .cb(cb7));
    // Output valid
    assign valid = valid_chain[7] || valid_chain[6] || valid_chain[5] || valid_chain[4] || valid_chain[3] || valid_chain[2] || valid_chain[1] || valid_chain[0];
    // Output code
    assign code = valid ? (
        (in[7] == 1'b1) ? 3'b111 :
        (in[6] == 1'b1) ? 3'b110 :
        (in[5] == 1'b1) ? 3'b101 :
        (in[4] == 1'b1) ? 3'b100 :
        (in[3] == 1'b1) ? 3'b011 :
        (in[2] == 1'b1) ? 3'b010 :
        (in[1] == 1'b1) ? 3'b001 : 3'b000
    ) : 3'b000;
endmodule
module check_bit(in, valid, code, cb);
    input [7:0] in;
    output valid;
    output [2:0] code;
    output [7:0] cb;
    wire [7:0] temp;
    // Check if current bit is 1
    assign cb[0] = in[0] && !in[1] && !in[2] && !in[3] && !in[4] && !in[5] && !in[6];
    // Check if previous bits are 1
    assign temp[0] = !in[1] && !in[2] && !in[3] && !in[4] && !in[5] && !in[6];
    assign cb[1] = in[1] && temp[0] && !in[3] && !in[4] && !in[5] && !in[6];
    assign temp[1] = !in[3] && !in[4] && !in[5] && !in[6];
    assign cb[2] = in[2] && temp[1] && !in[5] && !in[6];
    assign temp[2] = !in[5] && !in[6];
    assign cb[3] = in[3] && temp[2] && !in[6];
    assign temp[3] = !in[6];
    assign cb[4] = in[4] && temp[3];
    assign cb[5] = in[5] && temp[3];
    assign cb[6] = in[6];
    assign cb[7] = in[7];
    // Output valid
    assign valid = cb[0] || cb[1] || cb[2] || cb[3] || cb[4] || cb[5] || cb[6] || cb[7];
    // Output code
    assign code = valid ? (
        (in[7] == 1'b1) ? 3'b111 :
        (in[6] == 1'b1) ? 3'b110 :
        (in[5] == 1'b1) ? 3'b101 :
        (in[4] == 1'b1) ? 3'b100 :
        (in[3] == 1'b1) ? 3'b011 :
        (in[2] == 1'b1) ? 3'b010 :
        (in[1] == 1'b1) ? 3'b001 : 3'b000
    ) : 3'b000;
endmodule

