module systolic1x2 (
  input clk,
  input rst,
  input [15:0] a0,
  input [15:0] a1,
  input [15:0] b0,
  output [31:0] c0,
  output [31:0] c1
);
  wire [15:0] b1_w;
  wire [31:0] c0_w, c1_w;
  reg [15:0] b1;
  // PE 0
  pe pe0 (
    .clk(clk),
    .rst(rst),
    .a(a0),
    .b(b0),
    .c(c0_w)
  );
  // PE 1
  pe pe1 (
    .clk(clk),
    .rst(rst),
    .a(a1),
    .b(b1),
    .c(c1_w)
  );
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      b1 <= 16'b0;
    end else begin
      b1 <= b0;
    end
  end
  assign c0 = c0_w;
  assign c1 = c1_w;
endmodule
module pe (
  input clk,
  input rst,
  input [15:0] a,
  input [15:0] b,
  output reg [31:0] c
);
  wire [31:0] mult_out;
  assign mult_out = a * b;
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      c <= 32'b0;
    end else begin
      c <= c + mult_out;
    end
  end
endmodule

