module systolic1x2 (
  input clk,
  input rst,
  input [15:0] a0,
  input [15:0] a1,
  input [15:0] b0,
  output [31:0] c0,
  output [31:0] c1
);
  wire [15:0] pe0_a;
  wire [15:0] pe0_b;
  wire [31:0] pe0_c;
  wire [15:0] pe1_a;
  wire [15:0] pe1_b;
  wire [31:0] pe1_c;
  // PE 0
  assign pe0_a = a0;
  assign pe0_b = b0;
  pe pe0 (
    .clk(clk),
    .rst(rst),
    .a(pe0_a),
    .b(pe0_b),
    .c(pe0_c)
  );
  // PE 1
  reg [15:0] b1;
  assign pe1_a = a1;
  assign pe1_b = b1;
  pe pe1 (
    .clk(clk),
    .rst(rst),
    .a(pe1_a),
    .b(pe1_b),
    .c(pe1_c)
  );
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      b1 <= 16'b0;
    end else begin
      b1 <= b0;
    end
  end
  assign c0 = pe0_c;
  assign c1 = pe1_c;
endmodule
module pe (
  input clk,
  input rst,
  input [15:0] a,
  input [15:0] b,
  output reg [31:0] c
);
  reg [15:0] a_reg;
  reg [15:0] b_reg;
  reg [31:0] c_reg;
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      a_reg <= 16'b0;
      b_reg <= 16'b0;
      c_reg <= 32'b0;
    end else begin
      a_reg <= a;
      b_reg <= b;
      c_reg <= c_reg + (a_reg * b_reg);
    end
  end
  always @(posedge clk) begin
    c <= c_reg;
  end
endmodule

