    .clk(clk),
    .rst(rst),
    .rs1_out(rs1_out),
    .rs2_out(rs2_out),
    .op_out(op_out),
    .alu_out(alu_out)
  );
  memory_stage memory_stage_inst(
    .clk(clk),
