module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] pc_out
);
  reg [31:0] instr_out;
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      pc_out <= 32'h00000000;
      instr_out <= 32'h00000000;
    end else begin
      instr_out <= instr_mem[pc_out];
      pc_out <= pc_out + 1;
    end
  end
endmodule
module decode_stage(
  input clk,
  input rst,
  input [31:0] instr_out,
  output reg [31:0] rs1_out,
  output reg [31:0] rs2_out,
  output reg [31:0] rd_out,
  output reg [31:0] op_out
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      rs1_out <= 32'h00000000;
      rs2_out <= 32'h00000000;
      rd_out <= 32'h00000000;
      op_out <= 32'h00000000;
    end else begin
      rs1_out <= instr_out[19:15];
      rs2_out <= instr_out[24:20];
      rd_out <= instr_out[11:7];
      op_out <= instr_out[30];
    end
  end
