module execute_stage(
  input clk,
  input rst,
  input [31:0] rs1_out,
  input [31:0] rs2_out,
  input [31:0] op_out,
  output reg [31:0] alu_out
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      alu_out <= 32'h00000000;
    end else begin
      if (op_out) begin
        alu_out <= rs1_out - rs2_out;
      end else begin
        alu_out <= rs1_out + rs2_out;
