module modular_alu (
    input wire [1:0] op_code,
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] result,
    output wire zero
);
    wire [31:0] add_result;
    wire [31:0] subtract_result;
    wire [31:0] and_result;
    wire [31:0] or_result;
    u_adder adder (
        .a(a),
        .b(b),
        .result(add_result)
    );
    u_subtractor subtractor (
        .a(a),
        .b(b),
        .result(subtract_result)
    );
    u_and_logic and_logic (
        .a(a),
        .b(b),
        .result(and_result)
    );
    u_or_logic or_logic (
        .a(a),
        .b(b),
        .result(or_result)
    );
    assign result = (op_code == 2'b00) ? add_result :
                    (op_code == 2'b01) ? subtract_result :
                    (op_code == 2'b10) ? and_result :
                    (op_code == 2'b11) ? or_result :
                    32'b0;
    assign zero = (result == 32'b0) ? 1'b1 : 1'b0;
endmodule
module u_adder (
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] result
);
    assign result = a + b;
endmodule
module u_subtractor (
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] result
);
    assign result = a - b;
endmodule
module u_and_logic (
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] result
);
    assign result = a & b;
endmodule
module u_or_logic (
    input wire [31:0] a,
    input wire [31:0] b,
    output wire [31:0] result
);
    assign result = a | b;
endmodule

