module parity_16bit(X, Y, P);
    input [7:0] X, Y;
    output P;
    wire [7:0] P_out;
    // 8-bit parity generator blocks
    genvar i;
    generate
        for (i = 0; i < 8; i = i + 1) begin
            parity_8bit gen(X[7:0], Y[7:0], P_out[i]);
        end
    endgenerate
    // Final parity computation
    assign P = P_out[0] ^ P_out[1] ^ P_out[2] ^ P_out[3] ^ P_out[4] ^ P_out[5] ^ P_out[6] ^ P_out[7];
endmodule
module parity_8bit(X, Y, P);
