        for (i = 0; i < 8; i = i + 2) begin
            parity_4bit gen(X[i+1:i], Y[i+1:i], P_out[i>>1]);
        end
    endgenerate
    // Final parity computation
    assign P = P_out[0] ^ P_out[1];
