    genvar i;
    generate
        for (i = 0; i < 8; i = i + 1) begin
            if (i < 4) begin
                parity_4bit gen_inst(X[3:0], Y[3:0], P_4bit[i]);
            end
            else begin
                parity_4bit gen_inst(X[7:4], Y[7:4], P_4bit[i]);
            end
        end
    endgenerate
    // Final parity computation using XOR
    assign P = P_4bit[0] ^ P_4bit[1] ^ P_4bit[2] ^ P_4bit[3] ^ P_4bit[4] ^ P_4bit[5] ^ P_4bit[6] ^ P_4bit[7];
