    input [3:0] X, Y;
    output P;
    wire [3:0] P_2bit;
    // Instantiate 2-bit parity generator blocks
    genvar i;
    generate
        for (i = 0; i < 4; i = i + 1) begin
            parity_2bit gen_inst(X[1:0], Y[1:0], P_2bit[i]);
        end
    endgenerate
    // Final parity computation using XOR
    assign P = P_2bit[0] ^ P_2bit[1] ^ P_2bit[2] ^ P_2bit[3];
