module multi_function_alu (
    input wire [2:0] op_code,
    input wire [31:0] operand_a,
    input wire [31:0] operand_b,
    output wire [31:0] result,
    output wire zero
);
    wire [31:0] add_result;
    wire [31:0] sub_result;
    wire [31:0] and_result;
    wire [31:0] or_result;
    wire [31:0] xor_result;
    wire add_sel;
    wire sub_sel;
    wire and_sel;
    wire or_sel;
    wire xor_sel;
    assign add_sel = (op_code == 3'b000);
    assign sub_sel = (op_code == 3'b001);
    assign and_sel = (op_code == 3'b010);
    assign or_sel = (op_code == 3'b011);
    assign xor_sel = (op_code == 3'b100);
    adder u_adder (
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(add_result)
    );
    subtractor u_subtractor (
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(sub_result)
    );
    and_bit u_and_bit (
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(and_result)
    );
    or_bit u_or_bit (
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(or_result)
    );
    xor_bit u_xor_bit (
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(xor_result)
    );
    assign result = {32{add_sel}} & add_result |
                    {32{sub_sel}} & sub_result |
                    {32{and_sel}} & and_result |
                    {32{or_sel}} & or_result |
                    {32{xor_sel}} & xor_result;
    assign zero = (result == 32'b0);
endmodule
