module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] pc_out,
  output reg [31:0] pc_in,
  output reg [31:0] instr_out,
  input [31:0] instr_in
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      pc_out <= 32'b0;
      instr_out <= 32'b0;
    end else begin
      pc_out <= pc_in;
      instr_out <= instr_in;
    end
  end
endmodule
module decode_stage(
  input clk,
  input rst,
  input [31:0] instr_out,
  input [31:0] instr_in,
  output reg [4:0] rs1_out,
  output reg [4:0] rs1_in,
  output reg [4:0] rs2_out,
  output reg [4:0] rs2_in,
  output reg [4:0] rd_out,
  output reg [4:0] rd_in,
  output reg [2:0] op_out,
  output reg [2:0] op_in
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      rs1_out <= 5'b0;
      rs2_out <= 5'b0;
      rd_out <= 5'b0;
      op_out <= 3'b0;
    end else begin
      rs1_out <= instr_out[19:15];
      rs2_out <= instr_out[24:20];
      rd_out <= instr_out[11:7];
      op_out <= instr_out[30:25];
    end
  end
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      rs1_in <= 5'b0;
      rs2_in <= 5'b0;
      rd_in <= 5'b0;
      op_in <= 3'b0;
    end else begin
      rs1_in <= rs1_out;
      rs2_in <= rs2_out;
      rd_in <= rd_out;
      op_in <= op_out;
    end
  end
endmodule
module execute_stage(
  input clk,
  input rst,
  input [4:0] rs1_out,
  input [4:0] rs1_in,
  input [4:0] rs2_out,
  input [4:0] rs2_in,
  input [2:0] op_out,
  input [2:0] op_in,
