);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      alu_out <= 32'b0;
    end else begin
      alu_out <= alu_in;
    end
  end
endmodule
module memory_stage(
  input clk,
  input rst,
  input [31:0] alu_out,
  input [31:0] alu_in,
  output reg [31:0] alu_result
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      alu_result <= 32'b0;
    end else begin
      alu_result <= alu_out;
    end
  end
endmodule
module writeback_stage(
  input clk,
  input rst,
  input [31:0] alu_result,
  output reg [31:0] rf_out,
