module stagepipe5(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  input [31:0] reg_file [0:31],
  output [31:0] res_reg_file [0:31]
);
  wire [31:0] pc;
  wire [31:0] instr;
  wire [4:0] rs1;
  wire [4:0] rs2;
  wire [4:0] rd;
  wire [2:0] op;
  wire [31:0] alu_result;
  wire [31:0] rd_data;
  fetch_stage fetch_stage_inst(
    .clk(clk),
    .rst(rst),
    .instr_mem(instr_mem),
    .pc(pc)
  );
  decode_stage decode_stage_inst(
    .clk(clk),
    .rst(rst),
    .instr(instr),
    .rs1(rs1),
    .rs2(rs2),
    .rd(rd),
    .op(op)
  );
  execute_stage execute_stage_inst(
    .clk(clk),
    .rst(rst),
    .rs1(rs1),
    .rs2(rs2),
    .op(op),
    .alu_result(alu_result)
  );
  memory_stage memory_stage_inst(
    .clk(clk),
    .rst(rst),
    .alu_result(alu_result),
    .rd_data(rd_data)
  );
  writeback_stage writeback_stage_inst(
    .clk(clk),
    .rst(rst),
    .rd(rd),
    .rd_data(rd_data),
    .reg_file(reg_file),
    .res_reg_file(res_reg_file)
  );
endmodule
module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] pc
);
  reg [31:0] instr;
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      pc <= 32'h0;
      instr <= 32'h0;
    end else begin
      instr <= instr_mem[pc];
      pc <= pc + 1;
    end
  end
endmodule
module decode_stage(
  input clk,
  input rst,
  input [31:0] instr,
  output reg [4:0] rs1,
  output reg [4:0] rs2,
  output reg [4:0] rd,
  output reg [2:0] op
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      rs1 <= 5'b0;
      rs2 <= 5'b0;
      rd <= 5'b0;
      op <= 3'b0;
    end else begin
      rs1 <= instr[19:15];
      rs2 <= instr[24:20];
      rd <= instr[11:7];
      op <= instr[14:12];
    end
  end
endmodule
module execute_stage(
  input clk,
  input rst,
  input [4:0] rs1,
  input [4:0] rs2,
  input [2:0] op,
  output reg [31:0] alu_result
);
  reg [31:0] rs1_data;
  reg [31:0] rs2_data;
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      rs1_data <= 32'h0;
      rs2_data <= 32'h0;
      alu_result <= 32'h0;
    end else begin
      rs1_data <= rs1;
      rs2_data <= rs2;
      case (op)
        3'b000: alu_result <= rs1_data + rs2_data;
        3'b001: alu_result <= rs1_data - rs2_data;
        default: alu_result <= 32'h0;
      endcase
    end
  end
endmodule
module memory_stage(
  input clk,
  input rst,
  input [31:0] alu_result,
  output reg [31:0] rd_data
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      rd_data <= 32'h0;
    end else begin
      rd_data <= alu_result;
    end
  end
endmodule
module writeback_stage(
  input clk,
  input rst,
  input [4:0] rd,
  input [31:0] rd_data,
  input [31:0] reg_file [0:31],
  output reg [31:0] res_reg_file [0:31]
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      res_reg_file <= reg_file;
    end else begin
      res_reg_file[rd] <= rd_data;
    end
  end
endmodule

