  wire [31:0] alu_in1;
  wire [31:0] alu_in2;
  wire [31:0] alu_out;
  wire [31:0] rf_in;
  wire [31:0] rf_out;
  fetch_stage fetch_stage_inst(
    .clk(clk),
    .rst(rst),
    .instr_mem(instr_mem),
    .pc(pc),
    .instr(instr)
  );
  decode_stage decode_stage_inst(
    .instr(instr),
    .rs1(rs1),
    .rs2(rs2),
    .rd(rd),
    .op(op)
  );
  execute_stage execute_stage_inst(
    .rs1(rs1),
    .rs2(rs2),
    .op(op),
    .alu_in1(alu_in1),
    .alu_in2(alu_in2),
    .alu_out(alu_out)
  );
  memory_stage memory_stage_inst(
    .alu_out(alu_out),
    .rf_in(rf_in),
    .rf_out(rf_out)
  );
  writeback_stage writeback_stage_inst(
    .rf_out(rf_out),
    .res_reg_file(res_reg_file)
  );
endmodule
module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] pc,
  output reg [31:0] instr
);
  reg [31:0] next_pc;
  reg [31:0] next_instr;
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      pc <= 32'h0;
      instr <= 32'h0;
    end else begin
      pc <= next_pc;
      instr <= next_instr;
    end
  end
  always @(*) begin
    next_pc = pc + 4;
    next_instr = instr_mem[pc[31:2]];
  end
endmodule
module decode_stage(
  input [31:0] instr,
  output reg [4:0] rs1,
  output reg [4:0] rs2,
  output reg [4:0] rd,
  output reg [2:0] op
);
  reg [4:0] next_rs1;
  reg [4:0] next_rs2;
  reg [4:0] next_rd;
  reg [2:0] next_op;
  always @(*) begin
    next_rs1 = instr[19:15];
    next_rs2 = instr[24:20];
    next_rd = instr[11:7];
    next_op = instr[14:12];
  end
  always @(posedge clk) begin
    rs1 <= next_rs1;
    rs2 <= next_rs2;
    rd <= next_rd;
    op <= next_op;
  end
endmodule
module execute_stage(
  input [4:0] rs1,
  input [4:0] rs2,
  input [2:0] op,
  input [31:0] alu_in1,
  input [31:0] alu_in2,
  output reg [31:0] alu_out
);
  reg [31:0] next_alu_out;
  always @(*) begin
    case (op)
      3'b000: next_alu_out = alu_in1 + alu_in2;
      3'b001: next_alu_out = alu_in1 - alu_in2;
      default: next_alu_out = 32'h0;
    endcase
  end
  always @(posedge clk) begin
    alu_out <= next_alu_out;
  end
endmodule
module memory_stage(
  input [31:0] alu_out,
  input [31:0] rf_in,
  output reg [31:0] rf_out
);
  reg [31:0] next_rf_out;
  always @(*) begin
    next_rf_out = alu_out;
  end
  always @(posedge clk) begin
    rf_out <= next_rf_out;
  end
endmodule
module writeback_stage(
  input [31:0] rf_out,
  output reg [31:0] res_reg_file [0:31]
);
  reg [31:0] next_res_reg_file [0:31];
  always @(*) begin
    next_res_reg_file[rf_out[4:0]] = rf_out;
  end
  always @(posedge clk) begin
    res_reg_file <= next_res_reg_file;
  end
endmodule

