    reg [1:0] cnt;
    always @(posedge CLK_in or posedge RST) begin
        if (RST) begin
            CLK_out <= 1'b0;
            cnt <= 2'b0;
        end else begin
            if (cnt == 2'b11) begin
                CLK_out <= ~CLK_out;
                cnt <= 2'b0;
            end else begin
                cnt <= cnt + 1;
            end
        end
    end
