module decode_stage(
  input clk,
  input rst,
  input [31:0] instr,
  output reg [4:0] rs1,
  output reg [4:0] rs2,
  output reg [4:0] rd,
  output reg [2:0] op
);
  always @(posedge clk or posedge rst) begin
    if (rst) begin
      rs1 <= 5'b0;
      rs2 <= 5'b0;
      rd <= 5'b0;
      op <= 3'b0;
    end else begin
      rs1 <= instr[19:15];
      rs2 <= instr[24:20];
