);
  wire [15:0] b1, b2, b3;
  wire [31:0] c0_w, c1_w, c2_w, c3_w;
  // PE 0
  pe pe0(
    .clk(clk),
    .rst(rst),
    .a(a0),
    .b(b0),
    .c(c0_w)
  );
  // PE 1
  pe pe1(
    .clk(clk),
    .rst(rst),
    .a(a1),
    .b(b1),
