  input [15:0] a0,
  input [15:0] a1,
  input [15:0] a2,
  input [15:0] a3,
  input [15:0] b0,
  output [31:0] c0,
  output [31:0] c1,
  output [31:0] c2,
  output [31:0] c3 
);
  wire [15:0] pe0_a, pe0_b, pe1_a, pe1_b, pe2_a, pe2_b, pe3_a, pe3_b;
  wire [31:0] pe0_c, pe1_c, pe2_c, pe3_c;
  reg [15:0] b1, b2, b3;
  // Instantiate the PE modules
  pe pe0 (
    .clk(clk),
    .rst(rst),
    .a(pe0_a),
    .b(pe0_b),
    .c(pe0_c)
  );
  pe pe1 (
