module freq_divbyfrac(
    input               clk,
    input               rst_n,
    output              clk_div
);
    parameter MUL2_DIV_CLK = 7;
    wire clk_div_4;
    wire clk_div_3;
    counter u_counter(
        .clk    (clk),
        .rst_n  (rst_n),
        .limit  (MUL2_DIV_CLK),
        .clk_div(clk_div_4)
    );
    clk_average u_clk_average(
        .clk    (clk),
        .rst_n  (rst_n),
        .clk_div(clk_div_4),
        .clk_div_out(clk_div_3)
    );
    clk_adjustment u_clk_adjustment(
        .clk    (clk),
        .rst_n  (rst_n),
        .clk_div(clk_div_3),
        .clk_div_out(clk_div)
    );
endmodule
module counter(
    input               clk,
    input               rst_n,
    input [5:0]         limit,
    output              clk_div
);
    reg [5:0] cnt;
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            cnt <= 6'd0;
        end
        else if (cnt == limit - 1) begin
            cnt <= 6'd0;
        end
        else begin
            cnt <= cnt + 1'b1;
        end
    end
    assign clk_div = (cnt == 0) ? 1'b1 : 1'b0;
endmodule
module clk_adjustment(
    input               clk,
    input               rst_n,
    input               clk_div,
    output              clk_div_out
);
    reg clk_div_negedge;
    always @(negedge clk or negedge rst_n) begin
        if (!rst_n) begin
            clk_div_negedge <= 1'b0;
        end
        else begin
            clk_div_negedge <= clk_div;
        end
    end
    assign clk_div_out = clk_div | clk_div_negedge;
endmodule
module clk_average(
    input               clk,
    input               rst_n,
    input               clk_div,
    output              clk_div_out
);
    reg [1:0] cnt;
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            cnt <= 2'd0;
        end
        else if (clk_div) begin
            cnt <= cnt + 1'b1;
        end
    end
    assign clk_div_out = (cnt == 2'd0 || cnt == 2'd1) ? 1'b1 : 1'b0;
endmodule

