module mul_4bit(X, Y, P);
    input [3:0] X, Y;
    output [7:0] P;
    wire [3:0] pp0, pp1, pp2, pp3;
    wire [4:0] sum1, sum2, sum3;
    wire [5:0] sum4;
    wire [6:0] sum5;
    assign pp0 = {4{X[0]}} & Y[3:0];
    assign pp1 = {4{X[1]}} & Y[3:0];
    assign pp2 = {4{X[2]}} & Y[3:0];
    assign pp3 = {4{X[3]}} & Y[3:0];
    assign sum1 = pp0 + (pp1 << 1);
    assign sum2 = sum1 + (pp2 << 2);
    assign sum3 = sum2 + (pp3 << 3);
    assign P = sum3;
endmodule
module adder_8bit(A, B, Sum);
    input [7:0] A, B;
    output [7:0] Sum;
    wire [8:0] carry;
    genvar i;
    generate
        for (i = 0; i < 8; i = i + 1) begin
            if (i == 0) begin
                full_adder fa(.A(A[i]), .B(B[i]), .Cin(1'b0), .Sum(Sum[i]), .Cout(carry[i + 1]));
            end else begin
                full_adder fa(.A(A[i]), .B(B[i]), .Cin(carry[i]), .Sum(Sum[i]), .Cout(carry[i + 1]));
            end
        end
    endgenerate
endmodule
module full_adder(A, B, Cin, Sum, Cout);
    input A, B, Cin;
    output Sum, Cout;
    assign Sum = A ^ B ^ Cin;
    assign Cout = (A & B) | (A & Cin) | (B & Cin);
endmodule

module adder_32bit(
    input [31:0] X,    // First 32-bit input operand
    input [31:0] Y,    // Second 32-bit input operand
    output [31:0] Sum, // 32-bit output sum
    output Carry_out   // Carry output
);
    wire [2:0] carry; // Carry-out from each adder
    // Instantiate 4 8-bit adder submodules
    adder_8bit adder0(X[7:0], Y[7:0], Sum[7:0], carry[0]);
    adder_8bit adder1(X[15:8], Y[15:8], Sum[15:8], carry[1], carry[0]);
    adder_8bit adder2(X[23:16], Y[23:16], Sum[23:16], carry[2], carry[1]);
    adder_8bit adder3(X[31:24], Y[31:24], Sum[31:24], Carry_out, carry[2]);
endmodule
module adder_8bit(
    input [7:0] X,
    input [7:0] Y,
    output [7:0] Sum,
    output Carry_out,
    input Carry_in = 1'b0
);
    wire [7:0] carry;
    genvar i;
    generate
        for (i = 0; i < 8; i = i + 1) begin
            if (i == 0) begin
                full_adder fa(X[i], Y[i], Carry_in, Sum[i], carry[i]);
            end else begin
                full_adder fa(X[i], Y[i], carry[i - 1], Sum[i], carry[i]);
            end
        end
    endgenerate
    assign Carry_out = carry[7];
endmodule
module full_adder(
    input A,
    input B,
    input Cin,
    output Sum,
    output Cout
);
    assign Sum = A ^ B ^ Cin;
    assign Cout = (A & B) | (B & Cin) | (Cin & A);
endmodule

module freq_divbyodd(
    input clk,
    input rst_n,
    output clk_div
);
    parameter NUM_DIV = 5;
    wire clk_div1, clk_div2;
    pos_edge_counter u_pos_counter(
        .clk(clk),
        .rst_n(rst_n),
        .clk_div(clk_div1)
    );
    neg_edge_counter u_neg_counter(
        .clk(clk),
        .rst_n(rst_n),
        .clk_div(clk_div2)
    );
    assign clk_div = clk_div1 | clk_div2;
endmodule
module pos_edge_counter(
    input clk,
    input rst_n,
    output reg clk_div
);
    parameter NUM_DIV = 5;
    reg [3:0] cnt;
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            cnt <= 4'd0;
            clk_div <= 1'b0;
        end
        else begin
            if (cnt == NUM_DIV - 1) begin
                cnt <= 4'd0;
                clk_div <= 1'b1;
            end
            else begin
                cnt <= cnt + 1'b1;
                clk_div <= 1'b0;
            end
        end
    end
endmodule
module neg_edge_counter(
    input clk,
    input rst_n,
    output reg clk_div
);
    parameter NUM_DIV = 5;
    reg [3:0] cnt;
    always @(negedge clk or negedge rst_n) begin
        if (!rst_n) begin
            cnt <= 4'd0;
            clk_div <= 1'b0;
        end
        else begin
            if (cnt == NUM_DIV - 1) begin
                cnt <= 4'd0;
                clk_div <= 1'b1;
            end
            else begin
                cnt <= cnt + 1'b1;
                clk_div <= 1'b0;
            end
        end
    end
endmodule

module mul_4bit(X, Y, P);
    input [3:0] X, Y;
    output [7:0] P;
    wire [7:0] PP0, PP1, PP2, PP3;
    assign PP0 = {4'b0, X[0] & Y[0]};
    assign PP1 = {(X[1] & Y[0])} << 1;
    assign PP2 = {(X[2] & Y[0])} << 2;
    assign PP3 = {(X[3] & Y[0])} << 3;
    wire [7:0] PP4, PP5, PP6, PP7;
    assign PP4 = {4'b0, X[0] & Y[1]};
    assign PP5 = {(X[1] & Y[1])} << 1;
    assign PP6 = {(X[2] & Y[1])} << 2;
    assign PP7 = {(X[3] & Y[1])} << 3;
    wire [7:0] PP8, PP9, PP10, PP11;
    assign PP8 = {4'b0, X[0] & Y[2]};
    assign PP9 = {(X[1] & Y[2])} << 1;
    assign PP10 = {(X[2] & Y[2])} << 2;
    assign PP11 = {(X[3] & Y[2])} << 3;
    wire [7:0] PP12, PP13, PP14, PP15;
