    assign PP15 = {(X[3] & Y[3])} << 3;
    wire [7:0] PPall;
    assign PPall = PP0 + PP1 + PP2 + PP3 + PP4 + PP5 + PP6 + PP7 + PP8 + PP9 + PP10 + PP11 + PP12 + PP13 + PP14 + PP15;
