module adder_8bit(A, B, Cin, Sum, Cout);
    input [7:0] A, B;
    input Cin;
    output [7:0] Sum;
    output Cout;
    wire [7:0] C;
    genvar i;
    generate
        for (i = 0; i < 8; i = i + 1) begin
            if (i == 0)
                full_adder fa(A[i], B[i], Cin, Sum[i], C[i + 1]);
            else
                full_adder fa(A[i], B[i], C[i], Sum[i], C[i + 1]);
        end
    endgenerate
    assign Cout = C[7];
endmodule
module full_adder(A, B, Cin, Sum, Cout);
    input A, B, Cin;
    output Sum, Cout;
    assign Sum = A ^ B ^ Cin;
    assign Cout = (A & B) | (B & Cin) | (Cin & A);
endmodule

