    wire [31:0] sum_adder;      // Sum from each adder
    // Instantiate 4 8-bit adder submodules
    adder_8bit adder0(X[7:0], Y[7:0], Sum[7:0], carry_out_adder[0]);
    adder_8bit adder1(X[15:8], Y[15:8], Sum[15:8], carry_out_adder[1]);
    adder_8bit adder2(X[23:16], Y[23:16], Sum[23:16], carry_out_adder[2]);
    adder_8bit adder3(X[31:24], Y[31:24], Sum[31:24], Carry_out);
    // Connect the carry-out from each adder to the input carry-in of the next adder
    assign carry_out_adder[0] = 1'b0; // No carry-in for the first adder
    assign Sum[31:0] = sum_adder;
    // Carry-out from each adder
    assign Carry_out = carry_out_adder[2];
endmodule
