module adder_8bit(
    input [7:0] X,
    input [7:0] Y,
    output [7:0] Sum,
    output Carry_out,
    input Carry_in = 1'b0
);
    wire [7:0] carry;
    wire [7:0] gen;
    wire [7:0] prop;
    wire [7:0] sum;
    // Generate carry signals
    assign gen = X & Y;
    // Propagate carry signals
    assign prop = X ^ Y;
    // Calculate intermediate carries
    assign carry[0] = gen[0] | (prop[0] & Carry_in);
    assign carry[1] = gen[1] | (prop[1] & carry[0]);
    assign carry[2] = gen[2] | (prop[2] & carry[1]);
    assign carry[3] = gen[3] | (prop[3] & carry[2]);
    assign carry[4] = gen[4] | (prop[4] & carry[3]);
    assign carry[5] = gen[5] | (prop[5] & carry[4]);
    assign carry[6] = gen[6] | (prop[6] & carry[5]);
    assign carry[7] = gen[7] | (prop[7] & carry[6]);
    // Calculate sum
    assign sum = X ^ Y ^ Carry_in;
    // Outputs
    assign Sum = sum;
    assign Carry_out = carry[7];
endmodule

