module adder_32bit(
    input [31:0] X,    // First 32-bit input operand
    input [31:0] Y,    // Second 32-bit input operand
    output [31:0] Sum, // 32-bit output sum
    output Carry_out   // Carry output
);
    wire [2:0] carry_out_adder; // Carry-out from each adder
    wire [31:0] carry_in;       // Carry-in for each adder
    // Generate adders for each 8-bit segment
    adder_8bit adder0(.X(X[7:0]), .Y(Y[7:0]), .Sum(Sum[7:0]), .Carry_out(carry_out_adder[0]), .Carry_in(carry_in[0]));
    adder_8bit adder1(.X(X[15:8]), .Y(Y[15:8]), .Sum(Sum[15:8]), .Carry_out(carry_out_adder[1]), .Carry_in(carry_in[1]));
    adder_8bit adder2(.X(X[23:16]), .Y(Y[23:16]), .Sum(Sum[23:16]), .Carry_out(carry_out_adder[2]), .Carry_in(carry_in[2]));
    adder_8bit adder3(.X(X[31:24]), .Y(Y[31:24]), .Sum(Sum[31:24]), .Carry_out(Carry_out), .Carry_in(carry_in[3]));
    // Assign carry-in for the next adder
    assign carry_in[0] = 1'b0; // No carry-in for the first adder
    assign carry_in[1] = carry_out_adder[0];
    assign carry_in[2] = carry_out_adder[1];
    assign carry_in[3] = carry_out_adder[2];
endmodule
module adder_8bit(
    input [7:0] X,
    input [7:0] Y,
    input Carry_in,
    output [7:0] Sum,
    output Carry_out
);
    wire [6:0] carry_out;
    // Generate adders for each bit
    adder_1bit adder0(.X(X[0]), .Y(Y[0]), .Carry_in(Carry_in), .Sum(Sum[0]), .Carry_out(carry_out[0]));
    adder_1bit adder1(.X(X[1]), .Y(Y[1]), .Carry_in(carry_out[0]), .Sum(Sum[1]), .Carry_out(carry_out[1]));
    adder_1bit adder2(.X(X[2]), .Y(Y[2]), .Carry_in(carry_out[1]), .Sum(Sum[2]), .Carry_out(carry_out[2]));
    adder_1bit adder3(.X(X[3]), .Y(Y[3]), .Carry_in(carry_out[2]), .Sum(Sum[3]), .Carry_out(carry_out[3]));
    adder_1bit adder4(.X(X[4]), .Y(Y[4]), .Carry_in(carry_out[3]), .Sum(Sum[4]), .Carry_out(carry_out[4]));
    adder_1bit adder5(.X(X[5]), .Y(Y[5]), .Carry_in(carry_out[4]), .Sum(Sum[5]), .Carry_out(carry_out[5]));
    adder_1bit adder6(.X(X[6]), .Y(Y[6]), .Carry_in(carry_out[5]), .Sum(Sum[6]), .Carry_out(carry_out[6]));
    adder_1bit adder7(.X(X[7]), .Y(Y[7]), .Carry_in(carry_out[6]), .Sum(Sum[7]), .Carry_out(Carry_out));
endmodule
module adder_1bit(
    input X,
    input Y,
    input Carry_in,
    output Sum,
    output Carry_out
);
    wire sum_temp, carry_out_temp, carry_in_temp;
    // Sum logic
    xor sum_xor(sum_temp, X, Y);
    xor sum_result(Sum, sum_temp, Carry_in);
    // Carry-out logic
    and carry_out_and(carry_out_temp, X, Y);
    and carry_in_and(carry_in_temp, sum_temp, Carry_in);
    or carry_out_or(Carry_out, carry_out_temp, carry_in_temp);
endmodule

