    wire [3:0] adder_1_in_0, adder_1_in_1;
    wire [3:0] adder_2_in_0, adder_2_in_1;
    wire [3:0] adder_0_in_0_shifted, adder_0_in_1_shifted;
    wire [3:0] adder_1_in_0_shifted, adder_1_in_1_shifted;
    wire [3:0] adder_2_in_0_shifted, adder_2_in_1_shifted;
