    wire [3:0] adder_1_in_0_shifted_2, adder_1_in_1_shifted_2;
    wire [3:0] adder_2_in_0_shifted_2, adder_2_in_1_shifted_2;
    wire [3:0] adder_0_in_0_shifted_3, adder_0_in_1_shifted_3;
    wire [3:0] adder_1_in_0_shifted_3, adder_1_in_1_shifted_3;
    wire [3:0] adder_2_in_0_shifted_3, adder_2_in_1_shifted_3;
