    wire [3:0] adder_1_in_0_shifted_7, adder_1_in_1_shifted_7;
    wire [3:0] adder_2_in_0_shifted_7, adder_2_in_1_shifted_7;
    assign adder_0_in_0 = X;
    assign adder_0_in_1 = {4{Y[0]}} & adder_0_out;
    adder_4bit adder_0(adder_0_in_0, adder_0_in_1, adder_0_in_0_shifted, adder_0_in_1_shifted, adder_0_out);
    assign adder_1_in_0 = adder_0_in_0_shifted;
    assign adder_1_in_1 = {4{Y[1]}} & adder_1_out;
    adder_4bit adder_1(adder_1_in_0, adder_1_in_1, adder_1_in_0_shifted, adder_1_in_1_shifted, adder_1_out);
    assign adder_2_in_0 = adder_1_in_0_shifted;
    assign adder_2_in_1 = {4{Y[2]}} & adder_2_out;
    adder_4bit adder_2(adder_2_in_0, adder_2_in_1, adder_2_in_0_shifted, adder_2_in_1_shifted, adder_2_out);
    assign adder_0_in_0_shifted_2 = adder_2_in_0_shifted;
    assign adder_0_in_1_shifted_2 = {4{Y[0]}} & adder_0_out;
    adder_4bit adder_0_2(adder_0_in_0_shifted_2, adder_0_in_1_shifted_2, adder_0_in_0_shifted_3, adder_0_in_1_shifted_3, adder_0_out);
    assign adder_1_in_0_shifted_2 = adder_0_in_0_shifted_3;
    assign adder_1_in_1_shifted_2 = {4{Y[1]}} & adder_1_out;
    adder_4bit adder_1_2(adder_1_in_0_shifted_2, adder_1_in_1_shifted_2, adder_1_in_0_shifted_3, adder_1_in_1_shifted_3, adder_1_out);
    assign adder_2_in_0_shifted_2 = adder_1_in_0_shifted_3;
    assign adder_2_in_1_shifted_2 = {4{Y[2]}} & adder_2_out;
    adder_4bit adder_2_2(adder_2_in_0_shifted_2, adder_2_in_1_shifted_2, adder_2_in_0_shifted_3, adder_2_in_1_shifted_3, adder_2_out);
    assign adder_0_in_0_shifted_3 = adder_2_in_0_shifted_3;
    assign adder_0_in_1_shifted_3 = {4{Y[0]}} & adder_0_out;
    adder_4bit adder_0_3(adder_0_in_0_shifted_3, adder_0_in_1_shifted_3, adder_0_in_0_shifted_4, adder_0_in_1_shifted_4, adder_0_out);
