    assign adder_2_in_0_shifted_3 = adder_1_in_0_shifted_4;
    assign adder_2_in_1_shifted_3 = {4{Y[2]}} & adder_2_out;
    adder_4bit adder_2_3(adder_2_in_0_shifted_3, adder_2_in_1_shifted_3, adder_2_in_0_shifted_4, adder_2_in_1_shifted_4, adder_2_out);
