    assign clk_div_next = (cnt_end == 1'b1) ? 1'b1 : 1'b0;
    assign cnt = (rst_n == 1'b0) ? 4'd0 : (cnt_end ? 4'd0 : cnt + 1'b1);
    // Clock divider
    reg clk_div_reg;
    assign clk_div = clk_div_reg;
    always @(posedge clk or negedge rst_n) begin
        if (rst_n == 1'b0) begin
            clk_div_reg <= 1'b0;
        end else begin
            clk_div_reg <= clk_div_next;
        end
    end
