module u_counter (
    input               clk,
    input               rst_n,
    input               valid_in,
    output              valid_out,
    output      [2:0]   counter
);
    reg [2:0] counter_reg;
    reg valid_out_reg;
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            counter_reg <= 3'd0;
            valid_out_reg <= 1'b0;
        end else begin
            if (valid_in) begin
                if (counter_reg == 3'd4) begin
                    counter_reg <= 3'd0;
                    valid_out_reg <= 1'b1;
                end else begin
                    counter_reg <= counter_reg + 1'b1;
                    valid_out_reg <= 1'b0;
                end
            end else begin
                valid_out_reg <= 1'b0;
            end
        end
    end
    assign counter = counter_reg;
    assign valid_out = valid_out_reg;
