    wire [3:0] Y0, Y1, Y2, Y3;
    wire [7:0] P0, P1, P2, P3;
    assign X0 = {4{X[0]}} & 4'b0001;
    assign X1 = {4{X[1]}} & 4'b0010;
    assign X2 = {4{X[2]}} & 4'b0100;
    assign X3 = {4{X[3]}} & 4'b1000;
    assign Y0 = {4{Y[0]}} & 4'b0001;
    assign Y1 = {4{Y[1]}} & 4'b0010;
    assign Y2 = {4{Y[2]}} & 4'b0100;
    assign Y3 = {4{Y[3]}} & 4'b1000;
    assign P0 = X0 * Y0;
    assign P1 = (X1 * Y0) + (X0 * Y1);
    assign P2 = (X2 * Y0) + (X1 * Y1) + (X0 * Y2);
    assign P3 = (X3 * Y0) + (X2 * Y1) + (X1 * Y2) + (X0 * Y3);
    assign P = P0 + (P1 << 1) + (P2 << 2) + (P3 << 3);
endmodule
