module freq_divbyeven(
    input clk,
    input rst_n,
    output clk_div
);
    parameter NUM_DIV = 32'd100000000;
    wire clk_div_next;
    wire [3:0] cnt_next;
    reg [3:0] cnt;
    reg clk_div_reg;
    assign clk_div_next = (cnt == 4'd0)? ~clk_div_reg : clk_div_reg;
    assign cnt_next = (cnt == 4'd0)? 4'd0 : cnt - 4'd1;
    always @(posedge clk or negedge rst_n) begin
        if(~rst_n) begin
            cnt <= 4'd0;
            clk_div_reg <= 1'b0;
        end
        else begin
            cnt <= cnt_next;
            clk_div_reg <= clk_div_next;
        end
    end
    assign clk_div = clk_div_reg;
endmodule
module u_counter(
    input clk,
    input rst_n,
    input en,
    output reg [3:0] cnt
);
    always @(posedge clk or negedge rst_n) begin
        if(~rst_n)
            cnt <= 4'd0;
        else if(en)
            cnt <= cnt + 4'd1;
    end
endmodule
module u_clk_divider(
    input clk,
    input rst_n,
    input [3:0] cnt,
    output reg clk_div
);
    always @(posedge clk or negedge rst_n) begin
        if(~rst_n)
            clk_div <= 1'b0;
        else if(cnt == 4'd0)
            clk_div <= ~clk_div;
    end
endmodule

module adder_32bit(
    input [31:0] A,
    input [31:0] B,
    input Cin,
    output [31:0] Sum,
    output Cout
);
    wire [3:0] carry;
    adder_8bit adder0(A[7:0], B[7:0], Cin, Sum[7:0], carry[0]);
    adder_8bit adder1(A[15:8], B[15:8], carry[0], Sum[15:8], carry[1]);
    adder_8bit adder2(A[23:16], B[23:16], carry[1], Sum[23:16], carry[2]);
    adder_8bit adder3(A[31:24], B[31:24], carry[2], Sum[31:24], Cout);
endmodule
module adder_8bit(
    input [7:0] A,
    input [7:0] B,
    input Cin,
    output [7:0] Sum,
    output Cout
);
    wire [2:0] carry;
    adder_4bit adder0(A[3:0], B[3:0], Cin, Sum[3:0], carry[0]);
    adder_4bit adder1(A[7:4], B[7:4], carry[0], Sum[7:4], carry[1]);
    adder_4bit adder2(A[7:4], B[7:4], carry[1], Sum[7:4], carry[2]);
    assign Cout = carry[2];
endmodule
module adder_4bit(
    input [3:0] A,
    input [3:0] B,
    input Cin,
    output [3:0] Sum,
    output Cout
);
    wire [1:0] carry;
    adder_2bit adder0(A[1:0], B[1:0], Cin, Sum[1:0], carry[0]);
    adder_2bit adder1(A[3:2], B[3:2], carry[0], Sum[3:2], carry[1]);
    adder_2bit adder2(A[3:2], B[3:2], carry[1], Sum[3:2], Cout);
endmodule
module adder_2bit(
    input [1:0] A,
    input [1:0] B,
    input Cin,
    output [1:0] Sum,
    output Cout
);
    wire carry;
    adder_1bit adder0(A[0], B[0], Cin, Sum[0], carry);
    adder_1bit adder1(A[1], B[1], carry, Sum[1], Cout);
endmodule
module adder_1bit(
    input A,
    input B,
    input Cin,
    output Sum,
    output Cout
);
    assign Sum = A ^ B ^ Cin;
    assign Cout = (A & B) | (A & Cin) | (B & Cin);
endmodule

module mult_4bit(X, Y, P);
    input [3:0] X, Y;
    output [7:0] P;
    wire [3:0] adder_0_in1, adder_1_in1, adder_2_in1;
    wire [3:0] adder_0_in2, adder_1_in2, adder_2_in2;
    wire [3:0] adder_0_out, adder_1_out, adder_2_out;
    wire [3:0] adder_1_in1_d, adder_1_in2_d, adder_1_out_d;
    wire [3:0] adder_2_in1_d, adder_2_in2_d, adder_2_out_d;
