module freq_divbyeven(
    input clk,
    input rst_n,
    output clk_div
);
    parameter NUM_DIV = 4;
    wire clk_div_in;
    wire clk_div_out;
    u_counter u_counter(
        .clk(clk),
        .rst_n(rst_n),
        .clk_div_in(clk_div_in),
        .clk_div_out(clk_div_out)
    );
    u_clk_divider u_clk_divider(
        .clk(clk),
        .rst_n(rst_n),
        .clk_div_in(clk_div_in),
        .clk_div_out(clk_div_out),
        .clk_div(clk_div)
    );
endmodule
module u_counter(
    input clk,
    input rst_n,
    input clk_div_in,
    output reg clk_div_out
);
    parameter NUM_DIV = 4;
    reg [3:0] cnt;
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            cnt <= 4'd0;
            clk_div_out <= 1'b0;
        end
        else begin
            if (clk_div_in) begin
                if (cnt == (NUM_DIV / 2 - 1)) begin
                    cnt <= 4'd0;
                    clk_div_out <= ~clk_div_out;
                end
                else begin
                    cnt <= cnt + 1'b1;
                end
            end
        end
    end
endmodule
module u_clk_divider(
    input clk,
    input rst_n,
    input clk_div_in,
    input clk_div_out,
    output reg clk_div
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            clk_div <= 1'b0;
        end
        else begin
            if (clk_div_in) begin
                clk_div <= clk_div_out;
            end
        end
    end
endmodule

module adder_32bit(
    input [31:0] A,
    input [31:0] B,
    input Cin,
    output [31:0] Sum,
    output Cout
);
    wire [3:0] carry;
    adder_8bit adder0(A[7:0], B[7:0], Cin, Sum[7:0], carry[0]);
    adder_8bit adder1(A[15:8], B[15:8], carry[0], Sum[15:8], carry[1]);
    adder_8bit adder2(A[23:16], B[23:16], carry[1], Sum[23:16], carry[2]);
    adder_8bit adder3(A[31:24], B[31:24], carry[2], Sum[31:24], Cout);
endmodule
module adder_8bit(
    input [7:0] A,
    input [7:0] B,
    input Cin,
    output [7:0] Sum,
    output Cout
);
    wire [7:0] carry;
    adder_4bit adder0(A[3:0], B[3:0], Cin, Sum[3:0], carry[0]);
    adder_4bit adder1(A[7:4], B[7:4], carry[0], Sum[7:4], carry[1]);
    assign Cout = carry[1];
endmodule
module adder_4bit(
    input [3:0] A,
    input [3:0] B,
    input Cin,
    output [3:0] Sum,
    output Cout
);
    wire [3:0] carry;
    adder_2bit adder0(A[1:0], B[1:0], Cin, Sum[1:0], carry[0]);
    adder_2bit adder1(A[3:2], B[3:2], carry[0], Sum[3:2], carry[1]);
    assign Cout = carry[1];
endmodule
module adder_2bit(
    input [1:0] A,
    input [1:0] B,
    input Cin,
    output [1:0] Sum,
    output Cout
);
    wire [1:0] carry;
    adder_1bit adder0(A[0], B[0], Cin, Sum[0], carry[0]);
    adder_1bit adder1(A[1], B[1], carry[0], Sum[1], carry[1]);
    assign Cout = carry[1];
endmodule
module adder_1bit(
    input A,
    input B,
    input Cin,
    output Sum,
    output Cout
);
    assign Sum = A ^ B ^ Cin;
    assign Cout = (A & B) | (B & Cin) | (Cin & A);
endmodule

module freq_divbyeven(
    input clk,
    input rst_n,
    output clk_div
);
    parameter NUM_DIV = 32;
    wire u_counter_clk;
    wire u_counter_rst_n;
    wire [3:0] u_counter_q;
    wire u_clk_divider_clk;
    wire u_clk_divider_rst_n;
    wire u_clk_divider_en;
    // Connect submodules with each other
    u_counter u_counter(
        .clk(u_counter_clk),
        .rst_n(u_counter_rst_n),
        .q(u_counter_q)
    );
    u_clk_divider u_clk_divider(
        .clk(u_clk_divider_clk),
        .rst_n(u_clk_divider_rst_n),
        .en(u_clk_divider_en)
    );
    // Connect submodules with ports
    assign clk_div = u_clk_divider_clk;
    assign u_counter_clk = clk;
    assign u_counter_rst_n = rst_n;
