module freq_divbyeven(
    input clk,
    input rst_n,
    output clk_div
);
    parameter NUM_DIV = 32'd100000000;
    wire clk_div_next;
    wire [3:0] cnt_next;
    reg [3:0] cnt;
    reg clk_div_reg;
    assign clk_div_next = (cnt == 4'd0) ? ~clk_div_reg : clk_div_reg;
    assign cnt_next = (cnt == 4'd0) ? 4'd0 : (cnt - 1'd1);
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            cnt <= 4'd0;
            clk_div_reg <= 1'd0;
        end else begin
            cnt <= cnt_next;
            clk_div_reg <= clk_div_next;
        end
    end
    assign clk_div = clk_div_reg;
endmodule
module u_counter(
    input clk,
    input rst_n,
    input en,
    output reg [3:0] cnt
);
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            cnt <= 4'd0;
        end else if (en) begin
            cnt <= cnt + 1'd1;
        end
    end
endmodule
module u_clk_divider(
    input clk,
    input rst_n,
    input [3:0] cnt,
    output reg clk_div
);
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            clk_div <= 1'd0;
        end else if (cnt == 4'd0) begin
            clk_div <= ~clk_div;
        end
    end
endmodule

