module mult_4bit(X, Y, P);
    input [3:0] X, Y;
    output [7:0] P;
    wire [3:0] adder_0_out, adder_1_out, adder_2_out;
    wire [3:0] adder_0_in_0, adder_0_in_1;
    wire [3:0] adder_1_in_0, adder_1_in_1;
    wire [3:0] adder_2_in_0, adder_2_in_1;
    wire [3:0] adder_0_shift_in, adder_1_shift_in, adder_2_shift_in;
    wire [3:0] adder_0_shift_out, adder_1_shift_out, adder_2_shift_out;
    wire [3:0] adder_0_carry_out, adder_1_carry_out, adder_2_carry_out;
    wire [3:0] adder_0_sum, adder_1_sum, adder_2_sum;
    // adder_0
    assign adder_0_in_0 = X;
    assign adder_0_in_1 = {4{Y[0]}} & adder_0_shift_in;
    adder_4bit adder_0(
        .A(adder_0_in_0),
        .B(adder_0_in_1),
        .cin(1'b0),
        .sum(adder_0_sum),
        .cout(adder_0_carry_out),
        .P(adder_0_out)
    );
    // adder_1
    assign adder_1_in_0 = X;
    assign adder_1_in_1 = {4{Y[1]}} & adder_1_shift_in;
    adder_4bit adder_1(
        .A(adder_1_in_0),
        .B(adder_1_in_1),
        .cin(adder_0_carry_out),
        .sum(adder_1_sum),
        .cout(adder_1_carry_out),
        .P(adder_1_out)
    );
    // adder_2
    assign adder_2_in_0 = X;
    assign adder_2_in_1 = {4{Y[2]}} & adder_2_shift_in;
    adder_4bit adder_2(
        .A(adder_2_in_0),
        .B(adder_2_in_1),
        .cin(adder_1_carry_out),
        .sum(adder_2_sum),
        .cout(adder_2_carry_out),
        .P(adder_2_out)
    );
    // adder_3
    assign adder_3_in_0 = X;
    assign adder_3_in_1 = {4{Y[3]}} & adder_3_shift_in;
    adder_4bit adder_3(
        .A(adder_3_in_0),
        .B(adder_3_in_1),
        .cin(adder_2_carry_out),
        .sum(adder_3_sum),
        .cout(adder_3_carry_out),
        .P(adder_3_out)
    );
    // shift register for adder_0
    assign adder_0_shift_in = adder_0_out;
    assign adder_0_shift_out = adder_0_out >> 1;
    // shift register for adder_1
    assign adder_1_shift_in = adder_1_out;
    assign adder_1_shift_out = adder_1_out >> 1;
    // shift register for adder_2
    assign adder_2_shift_in = adder_2_out;
    assign adder_2_shift_out = adder_2_out >> 1;
    // shift register for adder_3
    assign adder_3_shift_in = adder_3_out;
    assign adder_3_shift_out = adder_3_out >> 1;
    // output
    assign P = {adder_3_out, adder_3_sum, adder_2_sum, adder_1_sum, adder_0_sum};
endmodule
