module simple_alu4bit(
    input clk,
    input [1:0] op_code,
    input [3:0] operand_a,
    input [3:0] operand_b,
    output reg [3:0] result
);
    wire [3:0] adder_result;
    wire [3:0] subtractor_result;
    wire [3:0] and_logical_result;
    wire [3:0] or_logical_result;
    // Instantiate submodules
    adder adder_inst(
        .clk(clk),
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(adder_result)
    );
    subtractor subtractor_inst(
        .clk(clk),
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(subtractor_result)
    );
    and_logical and_logical_inst(
        .clk(clk),
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(and_logical_result)
    );
    or_logical or_logical_inst(
        .clk(clk),
        .operand_a(operand_a),
        .operand_b(operand_b),
        .result(or_logical_result)
    );
    // Output mux
    always @(posedge clk) begin
        case (op_code)
            2'b00: result <= adder_result;
            2'b01: result <= subtractor_result;
            2'b10: result <= and_logical_result;
            2'b11: result <= or_logical_result;
        endcase
    end
endmodule
module adder(
    input clk,
    input [3:0] operand_a,
    input [3:0] operand_b,
    output reg [3:0] result
);
    always @(posedge clk) begin
        result <= operand_a + operand_b;
    end
endmodule
module subtractor(
    input clk,
    input [3:0] operand_a,
    input [3:0] operand_b,
    output reg [3:0] result
);
    always @(posedge clk) begin
        result <= operand_a - operand_b;
    end
endmodule
module and_logical(
    input clk,
    input [3:0] operand_a,
    input [3:0] operand_b,
    output reg [3:0] result
);
    always @(posedge clk) begin
        result <= operand_a & operand_b;
    end
endmodule
module or_logical(
    input clk,
    input [3:0] operand_a,
    input [3:0] operand_b,
    output reg [3:0] result
);
    always @(posedge clk) begin
        result <= operand_a | operand_b;
    end
endmodule

module pwm_generator(
    input clk,
    input [7:0] duty_cycle,
    input [7:0] freq,
    output pwm_out
);
    wire [15:0] count;
    pwm_counter pc(
        .clk(clk),
        .reset(1'b0),
        .freq(freq),
        .count(count)
    );
    pwm_signal ps(
        .clk(clk),
        .count(count),
        .duty_cycle(duty_cycle),
        .freq(freq),
        .pwm_out(pwm_out)
    );
endmodule
module pwm_counter(
    input clk,
    input reset,
    input [7:0] freq,
    output reg [15:0] count
);
    always @(posedge clk) begin
        if (reset) begin
            count <= 16'b0;
        end else if (count == freq - 1) begin
            count <= 16'b0;
        end else begin
            count <= count + 1;
        end
    end
endmodule
module pwm_signal(
    input clk,
    input [15:0] count,
    input [7:0] duty_cycle,
    input [7:0] freq,
    output reg pwm_out
);
    always @(posedge clk) begin
        if (count < freq * duty_cycle / 100) begin
            pwm_out <= 1'b1;
        end else begin
            pwm_out <= 1'b0;
        end
    end
endmodule

