module pwm_counter(
    input clk,
    input reset,
    input [7:0] freq,
    output [15:0] count
);
    reg [15:0] count_reg;
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            count_reg <= 16'b0;
        end else begin
            if (count_reg == freq - 1) begin
                count_reg <= 16'b0;
            end else begin
                count_reg <= count_reg + 1;
            end
        end
    end
    assign count = count_reg;
