    reg pwm_out_reg;
    always @(posedge clk) begin
        if (count < (freq * duty_cycle / 100)) begin
            pwm_out_reg <= 1'b1;
        end else begin
            pwm_out_reg <= 1'b0;
        end
    end
    assign pwm_out = pwm_out_reg;
endmodule

